function showPic(b){if(!document.getElementById("placeholder")){return true}var a=b.getAttribute("href");var e=document.getElementById("placeholder");e.setAttribute("src",a);var d=b.getAttribute("title");var c=document.getElementById("description");c.firstChild.nodeValue=d;return false}function prepareGallery(){if(!document.getElementsByTagName){return false}if(!document.getElementById){return false}if(!document.getElementById("imagegallery")){return false}var a=document.getElementById("imagegallery");var b=a.getElementsByTagName("a");for(var c=0;c<b.length;c++){b[c].onclick=function(){return showPic(this)}}}function addLoadEvent(a){var b=window.onload;if(typeof window.onload!="function"){window.onload=a}else{window.onload=function(){b();a()}}}addLoadEvent(prepareGallery);